  `imglink` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

--
-- Дамп данных таблицы `articles`
--

INSERT INTO `articles` (`id`, `title`, `text`, `imglink`) VALUES
(1, 'Загрязнение биосферы', 'Под загрязнением окружающей среды подразумевается повышенное содержание инородных веществ в биосфере. Это могут быть физические, биологические и химические реагенты, которые являются инородными и приводят к развитию различных негативных последствий. Уже многие десятки лет ученые из разных областей опасаются за состояние природы. Всё чаще звучат разговоры о возможной экологической катастрофе, в связи с серьезными климатическими изменениями и антропогенным воздействием человека. Некоторые страны начинают закупать воду, а в Китае появилась тенденция к массовой закупке баллонов с консервированным чистым воздухом. Люди начинают осознавать опасность экологической катастрофы, благодаря чему все большую популярность набирают различные экологические движения по сохранению природы и сокращению потребления.', './images/arictle-image.png'),
(2, 'Глобальное потепление и его последствия', 'Многие люди уже знают, что сегодня одной из существенных проблем является глобальное потепление. Стоит учесть, что существуют такие факторы, которые активизируют и ускоряют данный процесс. В первую очередь негативное влияние оказывает увеличение выброса в атмосферу углекислого газа, азота, метана и других вредных газов. Это происходит в результате деятельности промышленных предприятий, функционирования транспортных средств, но наибольшее влияние на окружающую среду происходит в ходе экологических катастроф: аварии на предприятиях, пожары, взрывы и утечки газов. Ускорению глобального потепления способствует выделение пара из-за высокой температуры воздуха. В результате этого активно испаряются воды рек, морей и океанов. Если этот процесс будет набирать обороты, то в течение трехсот лет океаны даже могут значительно иссушиться. Поскольку в результате глобального потепления таят ледники, это способствует повышению уровня воды в Мировом океане. В дальнейшем это подтапливает берега материков и островов, может привести к наводнению и разрушению населенных пунктов. Во время таяния льдов также выделяется газ метан, который значительно загрязняет атмосферу.', './images/gal1.jpg'),
(3, 'Красная книга Удмуртии', 'Растёт осознание важности защиты исчезающих видов. Живая природа находится под угрозой из-за охотников, деградации и разрушения мест обитания, токсичных сельскохозяйственных химикатов. Это главные риски для биома республики. Благодаря работе учёных приняты меры по защите исчезающих видов. Для проведения исследования местное правительство организовало комитет, который создал рабочие группы, каждая из которых состоит из экспертов, занимающихся одной из следующих таксономических групп: млекопитающие, птицы, рептилии и амфибии, пресноводные рыбы, насекомые, декаподы и улитки, беспозвоночные, растения.', './images/gal6.jpg');

